
<script setup lang="ts">
const reportsStore = useReportsStore();
const { fetchReports } = reportsStore;
const { reports } = storeToRefs(reportsStore);

const reportsSorted = computed(() => {
  return reports.value.sort( (a, b) => {
    return b.id - a.id;
  });
})

const classStore = useClassesStore();
const { fetchClass } = classStore;

const route = useRoute();
const projectId = computed(() => {
  return +route.params.id;
});

const classId = computed(() => {
  return +route.query?.classId!
});

await fetchClass(classId.value)
await fetchReports(projectId.value);
</script>
<template>
  <div class="relative px-4 overflow-y-scroll" v-if="reports.length">
    <div class="stars w-screen fixed pointer-events-none inset-x-0 -top-[--header-height] bottom-0 opacity-75">
      <div class="h-px w-px rounded-full bg-transparent" />
      <div class="h-px w-px rounded-full bg-transparent" />
      <div class="h-px w-px rounded-full bg-transparent" />
    </div>

    <div>
      <div class="h-[96px] w-0.5 bg-gray-200 mx-auto rounded-t-full" />
      <div v-for="(report, index) in reportsSorted" :key="index" class="relative py-3 min-h-[24px] flex items-center justify-center">
        <div class="h-full w-0.5 bg-gray-200 absolute top-0 inset-x-[50%] -ml-[1px] flex-shrink-0" />
          <div class="flex items-start gap-8 relative w-[50%]" :class="index % 2 === 0 ? 'translate-x-[50%] -ml-2' : '-translate-x-[50%] ml-2 flex-row-reverse'">
            <div class="h-[8px] w-[8px] bg-gray-400 rounded-full z-[1] mt-2 ring-2 ring-gray-30 flex-shrink-0" />
            <ReportsItem :report="report" :class="index % 2 === 0 ? '' : 'text-right'" />
          </div>
      </div>
    </div>
  </div>
</template>

